mapper.select:
select count(1)
from message_text mt
left join message m on mt.id = m.message_text_id and m.is_deleted = #{isDeleted,jdbcType=INTEGER} and m.receiver_id = #{receiverId,jdbcType=BIGINT}


    and mt.id = #{messageTextId,jdbcType=BIGINT}


    and mt.type = #{type,jdbcType=INTEGER}


    and mt.is_deleted = #{isDeleted,jdbcType=INTEGER}

SELECT mt.id message_text_id, mt.content, mt.type, mt.send_time, mt.is_deleted msg_is_deleted, mt.title, m.id, m.sender_id, m.receiver_id, ifnull(m.status, 0), m.create_time, m.is_deleted, m.create_time, m.update_time FROM message_text mt left join message m on mt.id = m.message_text_id and m.is_deleted = #{isDeleted,jdbcType=INTEGER} and m.receiver_id = #{receiverId,jdbcType=BIGINT}
and mt.id = #{messageTextId,jdbcType=BIGINT}
and mt.type = #{type,jdbcType=INTEGER}
and mt.is_deleted = #{isDeleted,jdbcType=INTEGER}
order by mt.id desc limit #{start}, #{rows}

SELECT mt.id message_text_id, mt.content, mt.type, mt.send_time, mt.is_deleted msg_is_deleted, mt.title, m.id, m.sender_id, m.receiver_id, ifnull(m.status, 0), m.create_time, m.is_deleted, m.create_time, m.update_time FROM message_text mt left join message m on mt.id = m.message_text_id and m.receiver_id = #{receiverId,jdbcType=BIGINT}
and mt.id = #{messageTextId,jdbcType=BIGINT}
and mt.type = #{type,jdbcType=INTEGER}

select

from message
where receiver_id = #{receiverId,jdbcType=BIGINT}
  and message_text_id = #{messageTextId,jdbcType=BIGINT}

SELECT mt.id message_text_id, mt.content, mt.type, mt.send_time, mt.is_deleted msg_is_deleted, mt.title, mt.act_id, mt.url, m.id, m.sender_id, m.receiver_id, ifnull(m.status, 0), m.create_time, m.is_deleted, m.create_time, m.update_time, m.status FROM message_text mt left join message m on mt.id = m.message_text_id and m.receiver_id = #{receiverId,jdbcType=BIGINT}
mt.is_deleted=0 and ((m.is_deleted=0 and m.receiver_id = #{receiverId,jdbcType=BIGINT}) or (m.is_deleted is null and mt.type in (1,2)))
and mt.id = #{messageTextId,jdbcType=BIGINT}
and mt.type = #{type,jdbcType=INTEGER}
order by m.status asc, mt.send_time desc limit #{start}, #{rows}

SELECT count(1)
FROM
message_text mt
left join message m on mt.id = m.message_text_id  and m.receiver_id = #{receiverId,jdbcType=BIGINT}

  mt.is_deleted=0
  and (m.is_deleted=0 or m.is_deleted is null)

    and mt.id = #{messageTextId,jdbcType=BIGINT}


    and mt.type = #{type,jdbcType=INTEGER}

select count(1) from (
select mt.id from message_text mt left join message m on m.message_text_id=mt.id and (m.receiver_id=#{receiverId,jdbcType=BIGINT} or m.receiver_id is null) where (m.status=0 or m.status is null) and (m.is_deleted=0 or m.is_deleted is null) and mt.is_deleted=0 and mt.type=#{type,jdbcType=INTEGER}
select mt.id from message_text mt left join message m on m.message_text_id=mt.id where m.status=0 and m.is_deleted=0 and mt.is_deleted=0 and m.receiver_id=#{receiverId,jdbcType=BIGINT} and mt.type=#{type,jdbcType=INTEGER}
) s
