mapper.select:
SELECT s.id, s.salepoint_name, s.address, s.mobile, s.phone, s.index_url, s.logo_url, s.salepoint_info, s.location_x, s.location_y, s.is_deleted, s.remark, s.create_time, s.update_time, s.apply_status, s.business_cate, s.salepoint_code, s.salepoint_hours, s.other_service, s.city, s.type, s.chain_type,
ROUND( 6378.138 * 2 * ASIN( SQRT( POW( SIN( (
#{locationY} * PI() / 180 - s.location_y * PI() / 180 ) / 2 ), 2 ) + COS( #{locationY} * PI() / 180) * COS(s.location_y * PI() / 180) * POW( SIN( (
#{locationX} * PI() / 180 - s.location_x * PI() / 180 ) / 2 ), 2 ) ) ) * 1000 ) 0
AS distance, ' ' as rule_desc FROM salepoint s
1=1 and s.is_deleted = 0
and (s.id = #{searchKey} or salepoint_name like concat('%', #{searchKey}, '%'))
and s.salepoint_name like concat('%', #{salepointName}, '%')
and s.city = #{city}
and s.chain_type = #{chainType}
and s.type = #{type}
AND s.salepoint_code = #{salepointCode,jdbcType=VARCHAR}
order by distance limit #{start}, #{rows}

select count(1) from salepoint s

  1=1 and s.is_deleted = 0

    and (s.id = #{searchKey} or salepoint_name like concat('%', #{searchKey}, '%'))


    and s.salepoint_name like concat('%', #{salepointName}, '%')


    and s.city = #{city}


    and s.type = #{type}


    AND s.salepoint_code = #{salepointCode,jdbcType=VARCHAR}


    and s.chain_type = #{chainType}

select
s.*,

   ROUND(
    6378.138 * 2 * ASIN(
    SQRT(
    POW(
    SIN(
    (

      #{locationY}
     * PI() / 180 - s.location_y * PI() / 180
    ) / 2
    ),
    2
    ) + COS(
      #{locationY}
     * PI() / 180) * COS(s.location_y * PI() / 180) * POW(
    SIN(
    (

      #{locationX}
     * PI() / 180 - s.location_x * PI() / 180
    ) / 2
    ),
    2
    )
    )
    ) * 1000
    )
  0

AS distance
from salepoint s
left join salepoint_goods sg
on s.id=sg.salepoint_id
where sg.sku_id= #{goodsSkuId}
and  s.is_deleted = 0
